import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
import * as schema from "@shared/schema";

neonConfig.webSocketConstructor = ws;

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

export const pool = new Pool({ connectionString: process.env.DATABASE_URL });
export const db = drizzle({ client: pool, schema });

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/replitAuth.ts.content:
import * as client from "openid-client";
import { Strategy, type VerifyFunction } from "openid-client/passport";

import passport from "passport";
import session from "express-session";
import type { Express, RequestHandler } from "express";
import memoize from "memoizee";
import connectPg from "connect-pg-simple";
import { storage } from "./storage";

if (!process.env.REPLIT_DOMAINS) {
  throw new Error("Environment variable REPLIT_DOMAINS not provided");
}

const getOidcConfig = memoize(
  async () => {
    return await client.discovery(
      new URL(process.env.ISSUER_URL ?? "https://replit.com/oidc"),
      process.env.REPL_ID!
    );
  },
  { maxAge: 3600 * 1000 }
);

export function getSession() {
  const sessionTtl = 7 * 24 * 60 * 60 * 1000; // 1 week
  const pgStore = connectPg(session);
  const sessionStore = new pgStore({
    conString: process.env.DATABASE_URL,
    createTableIfMissing: false,
    ttl: sessionTtl,
    tableName: "sessions",
  });
  return session({
    secret: process.env.SESSION_SECRET!,
    store: sessionStore,
    resave: false,
    saveUninitialized: false,
    cookie: {
      httpOnly: true,
      secure: true,
      maxAge: sessionTtl,
    },
  });
}

function updateUserSession(
  user: any,
  tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers
) {
  user.claims = tokens.claims();
  user.access_token = tokens.access_token;
  user.refresh_token = tokens.refresh_token;
  user.expires_at = user.claims?.exp;
}

async function upsertUser(
  claims: any,
) {
  await storage.upsertUser({
    email: claims["email"],
    firstName: claims["first_name"],
    lastName: claims["last_name"],
    profileImageUrl: claims["profile_image_url"],
  });
}

export async function setupAuth(app: Express) {
  app.set("trust proxy", 1);
  app.use(getSession());
  app.use(passport.initialize());
  app.use(passport.session());

  const config = await getOidcConfig();

  const verify: VerifyFunction = async (
    tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers,
    verified: passport.AuthenticateCallback
  ) => {
    const user = {};
    updateUserSession(user, tokens);
    await upsertUser(tokens.claims());
    verified(null, user);
  };

  for (const domain of process.env
    .REPLIT_DOMAINS!.split(",")) {
    const strategy = new Strategy(
      {
        name: `replitauth:${domain}`,
        config,
        scope: "openid email profile offline_access",
        callbackURL: `https://${domain}/api/callback`,
      },
      verify,
    );
    passport.use(strategy);
  }

  passport.serializeUser((user: Express.User, cb) => cb(null, user));
  passport.deserializeUser((user: Express.User, cb) => cb(null, user));

  app.get("/api/login", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      prompt: "login consent",
      scope: ["openid", "email", "profile", "offline_access"],
    })(req, res, next);
  });

  app.get("/api/callback", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      successReturnToOrRedirect: "/",
      failureRedirect: "/api/login",
    })(req, res, next);
  });

  app.get("/api/logout", (req, res) => {
    req.logout(() => {
      res.redirect(
        client.buildEndSessionUrl(config, {
          client_id: process.env.REPL_ID!,
          post_logout_redirect_uri: `${req.protocol}://${req.hostname}`,
        }).href
      );
    });
  });
}

export const isAuthenticated: RequestHandler = async (req, res, next) => {
  const user = req.user as any;

  if (!req.isAuthenticated() || !user.expires_at) {
    return res.status(401).json({ message: "Unauthorized" });
  }

  const now = Math.floor(Date.now() / 1000);
  if (now <= user.expires_at) {
    return next();
  }

  const refreshToken = user.refresh_token;
  if (!refreshToken) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }

  try {
    const config = await getOidcConfig();
    const tokenResponse = await client.refreshTokenGrant(config, refreshToken);
    updateUserSession(user, tokenResponse);
    return next();
  } catch (error) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }
};

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { setupAuth, isAuthenticated } from "./replitAuth";
import { insertApplicationSchema, insertBenchmarkSchema } from "@shared/schema";
import { benchmarkService } from "./services/benchmarkService";
import { aiService } from "./services/aiService";
import { reliabilityService } from "./services/reliabilityService";

// Helper function to get user ID from authenticated request
async function getUserIdFromRequest(req: any): Promise<string | null> {
  try {
    const userEmail = req.user.claims.email;
    const user = await storage.getUserByEmail(userEmail);
    return user?.id || null;
  } catch (error) {
    console.error("Error getting user ID:", error);
    return null;
  }
}

export async function registerRoutes(app: Express): Promise<Server> {
  // Auth middleware
  await setupAuth(app);

  // Auth routes
  app.get('/api/auth/user', isAuthenticated, async (req: any, res) => {
    try {
      const userEmail = req.user.claims.email;
      const user = await storage.getUserByEmail(userEmail);
      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }
      res.json(user);
    } catch (error) {
      console.error("Error fetching user:", error);
      res.status(500).json({ message: "Failed to fetch user" });
    }
  });

  // Dashboard routes
  app.get('/api/dashboard/stats', isAuthenticated, async (req: any, res) => {
    try {
      const userId = await getUserIdFromRequest(req);
      if (!userId) {
        return res.status(404).json({ message: "User not found" });
      }
      const stats = await storage.getDashboardStats(userId);
      res.json(stats);
    } catch (error) {
      console.error("Error fetching dashboard stats:", error);
      res.status(500).json({ message: "Failed to fetch dashboard stats" });
    }
  });

  // Application routes
  app.get('/api/applications', isAuthenticated, async (req: any, res) => {
    try {
      const userId = await getUserIdFromRequest(req);
      if (!userId) {
        return res.status(404).json({ message: "User not found" });
      }
      const applications = await storage.getApplicationsByUserId(userId);

      // Fetch latest metrics for each application
      const applicationsWithMetrics = await Promise.all(
        applications.map(async (app) => {
          const [performance, integration, reliability] = await Promise.all([
            storage.getLatestPerformanceMetrics(app.id),
            storage.getIntegrationAnalysis(app.id),
            storage.getLatestReliabilityMetrics(app.id)
          ]);

          return {
            ...app,
            performance,
            integration,
            reliability
          };
        })
      );

      res.json(applicationsWithMetrics);
    } catch (error) {
      console.error("Error fetching applications:", error);
      res.status(500).json({ message: "Failed to fetch applications" });
    }
  });

  app.post('/api/applications', isAuthenticated, async (req: any, res) => {
    try {
      const userId = await getUserIdFromRequest(req);
      if (!userId) {
        return res.status(404).json({ message: "User not found" });
      }
      const data = insertApplicationSchema.parse({ ...req.body, userId });

      const application = await storage.createApplication(data);

      // Start AI analysis of documentation in background
      if (data.documentationUrl) {
        aiService.analyzeIntegrations(application.id, data.documentationUrl)
          .catch(err => console.error('AI analysis failed:', err));
      }

      // Start reliability monitoring
      reliabilityService.startMonitoring(application.id, data.url)
        .catch(err => console.error('Reliability monitoring failed:', err));

      res.json(application);
    } catch (error) {
      console.error("Error creating application:", error);
      res.status(400).json({ message: "Invalid application data" });
    }
  });

  app.get('/api/applications/:id', isAuthenticated, async (req: any, res) => {
    try {
      const application = await storage.getApplicationById(req.params.id);
      if (!application) {
        return res.status(404).json({ message: "Application not found" });
      }

      // Verify ownership
      const userId = await getUserIdFromRequest(req);
      if (!userId || application.userId !== userId) {
        return res.status(403).json({ message: "Access denied" });
      }

      const [performance, integration, reliability] = await Promise.all([
        storage.getLatestPerformanceMetrics(application.id),
        storage.getIntegrationAnalysis(application.id),
        storage.getLatestReliabilityMetrics(application.id)
      ]);

      res.json({
        ...application,
        performance,
        integration,
        reliability
      });
    } catch (error) {
      console.error("Error fetching application:", error);
      res.status(500).json({ message: "Failed to fetch application" });
    }
  });

  app.delete('/api/applications/:id', isAuthenticated, async (req: any, res) => {
    try {
      const application = await storage.getApplicationById(req.params.id);
      if (!application) {
        return res.status(404).json({ message: "Application not found" });
      }

      // Verify ownership
      const userId = await getUserIdFromRequest(req);
      if (!userId || application.userId !== userId) {
        return res.status(403).json({ message: "Access denied" });
      }

      await storage.deleteApplication(req.params.id);
      res.json({ message: "Application deleted successfully" });
    } catch (error) {
      console.error("Error deleting application:", error);
      res.status(500).json({ message: "Failed to delete application" });
    }
  });

  // Benchmark routes
  app.get('/api/benchmarks', isAuthenticated, async (req: any, res) => {
    try {
      const userId = await getUserIdFromRequest(req);
      if (!userId) {
        return res.status(404).json({ message: "User not found" });
      }
      const benchmarks = await storage.getBenchmarksByUserId(userId);
      res.json(benchmarks);
    } catch (error) {
      console.error("Error fetching benchmarks:", error);
      res.status(500).json({ message: "Failed to fetch benchmarks" });
    }
  });

  app.post('/api/benchmarks', isAuthenticated, async (req: any, res) => {
    try {
      const userId = await getUserIdFromRequest(req);
      if (!userId) {
        return res.status(404).json({ message: "User not found" });
      }
      const data = insertBenchmarkSchema.parse({ ...req.body, userId });

      // Verify application ownership
      const application = await storage.getApplicationById(data.applicationId);
      if (!application || application.userId !== userId) {
        return res.status(403).json({ message: "Access denied" });
      }

      const benchmark = await storage.createBenchmark(data);

      // Start benchmark test
      benchmarkService.runBenchmark(benchmark.id, application.url, data.type, data.duration)
        .catch(err => console.error('Benchmark failed:', err));

      res.json(benchmark);
    } catch (error) {
      console.error("Error creating benchmark:", error);
      res.status(400).json({ message: "Invalid benchmark data" });
    }
  });

  app.get('/api/benchmarks/application/:applicationId', isAuthenticated, async (req: any, res) => {
    try {
      const userId = await getUserIdFromRequest(req);
      const application = await storage.getApplicationById(req.params.applicationId);

      if (!application || application.userId !== userId) {
        return res.status(403).json({ message: "Access denied" });
      }

      const benchmarks = await storage.getBenchmarksByApplicationId(req.params.applicationId);
      res.json(benchmarks);
    } catch (error) {
      console.error("Error fetching application benchmarks:", error);
      res.status(500).json({ message: "Failed to fetch benchmarks" });
    }
  });

  // Performance metrics routes
  app.get('/api/performance/:applicationId', isAuthenticated, async (req: any, res) => {
    try {
      const userId = await getUserIdFromRequest(req);
      const application = await storage.getApplicationById(req.params.applicationId);

      if (!application || application.userId !== userId) {
        return res.status(403).json({ message: "Access denied" });
      }

      const metrics = await storage.getPerformanceMetricsHistory(req.params.applicationId);
      res.json(metrics);
    } catch (error) {
      console.error("Error fetching performance metrics:", error);
      res.status(500).json({ message: "Failed to fetch performance metrics" });
    }
  });

  // Export routes
  app.get('/api/export/applications', isAuthenticated, async (req: any, res) => {
    try {
      const userId = await getUserIdFromRequest(req);
      if (!userId) {
        return res.status(404).json({ message: "User not found" });
      }
      const format = req.query.format || 'csv';

      const applications = await storage.getApplicationsByUserId(userId);

      if (format === 'csv') {
        const csvData = await generateCSVExport(applications, storage);
        res.setHeader('Content-Type', 'text/csv');
        res.setHeader('Content-Disposition', 'attachment; filename="applications-export.csv"');
        res.send(csvData);
      } else {
        res.status(400).json({ message: "Unsupported export format" });
      }
    } catch (error) {
      console.error("Error exporting applications:", error);
      res.status(500).json({ message: "Failed to export applications" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

async function generateCSVExport(applications: any[], storage: any): Promise<string> {
  const headers = [
    'Name',
    'URL',
    'Category',
    'Status',
    'Performance Score',
    'Response Time (ms)',
    'Uptime (%)',
    'Error Rate (%)',
    'Integrations',
    'Documentation Quality',
    'Created At'
  ];

  const rows = await Promise.all(
    applications.map(async (app) => {
      const [performance, integration] = await Promise.all([
        storage.getLatestPerformanceMetrics(app.id),
        storage.getIntegrationAnalysis(app.id)
      ]);

      return [
        app.name,
        app.url,
        app.category,
        app.status,
        performance?.performanceScore || 'N/A',
        performance?.responseTime || 'N/A',
        performance?.uptime || 'N/A',
        performance?.errorRate || 'N/A',
        integration?.integrationCount || 'N/A',
        integration?.documentationQuality || 'N/A',
        app.createdAt?.toISOString() || 'N/A'
      ];
    })
  );

  const csvContent = [headers, ...rows]
    .map(row => row.map(cell => `"${cell}"`).join(','))
    .join('\n');

  return csvContent;
}

server/storage.ts.content:
import {
  users,
  applications,
  benchmarks,
  performanceMetrics,
  integrationAnalysis,
  reliabilityMetrics,
  type User,
  type UpsertUser,
  type Application,
  type InsertApplication,
  type Benchmark,
  type InsertBenchmark,
  type PerformanceMetrics,
  type InsertPerformanceMetrics,
  type IntegrationAnalysis,
  type InsertIntegrationAnalysis,
  type ReliabilityMetrics,
  type InsertReliabilityMetrics
} from "@shared/schema";
import { db } from "./db";
import { eq, desc, and, sql } from "drizzle-orm";

export interface IStorage {
  // User operations (mandatory for Replit Auth)
  getUser(id: string): Promise<User | undefined>;
  getUserByEmail(email: string): Promise<User | undefined>;
  upsertUser(user: UpsertUser): Promise<User>;

  // Application operations
  createApplication(application: InsertApplication): Promise<Application>;
  getApplicationsByUserId(userId: string): Promise<Application[]>;
  getApplicationById(id: string): Promise<Application | undefined>;
  updateApplicationStatus(id: string, status: string): Promise<void>;
  deleteApplication(id: string): Promise<void>;

  // Benchmark operations
  createBenchmark(benchmark: InsertBenchmark): Promise<Benchmark>;
  getBenchmarksByApplicationId(applicationId: string): Promise<Benchmark[]>;
  getBenchmarksByUserId(userId: string): Promise<Benchmark[]>;
  updateBenchmark(id: string, data: Partial<Benchmark>): Promise<void>;

  // Performance metrics operations
  createPerformanceMetrics(metrics: InsertPerformanceMetrics): Promise<PerformanceMetrics>;
  getLatestPerformanceMetrics(applicationId: string): Promise<PerformanceMetrics | undefined>;
  getPerformanceMetricsHistory(applicationId: string): Promise<PerformanceMetrics[]>;

  // Integration analysis operations
  upsertIntegrationAnalysis(analysis: InsertIntegrationAnalysis): Promise<IntegrationAnalysis>;
  getIntegrationAnalysis(applicationId: string): Promise<IntegrationAnalysis | undefined>;

  // Reliability metrics operations
  createReliabilityMetrics(metrics: InsertReliabilityMetrics): Promise<ReliabilityMetrics>;
  getLatestReliabilityMetrics(applicationId: string): Promise<ReliabilityMetrics | undefined>;
  getReliabilityMetricsHistory(applicationId: string): Promise<ReliabilityMetrics[]>;

  // Dashboard operations
  getDashboardStats(userId: string): Promise<{
    totalApplications: number;
    averageResponseTime: number;
    averageUptime: number;
    activeBenchmarks: number;
  }>;
}

export class DatabaseStorage implements IStorage {
  // User operations
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user;
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.email, email));
    return user;
  }

  async upsertUser(userData: UpsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(userData)
      .onConflictDoUpdate({
        target: users.email,
        set: {
          firstName: userData.firstName,
          lastName: userData.lastName,
          profileImageUrl: userData.profileImageUrl,
          updatedAt: new Date(),
        },
      })
      .returning();
    return user;
  }

  // Application operations
  async createApplication(application: InsertApplication): Promise<Application> {
    const [app] = await db
      .insert(applications)
      .values(application)
      .returning();
    return app;
  }

  async getApplicationsByUserId(userId: string): Promise<Application[]> {
    return await db
      .select()
      .from(applications)
      .where(eq(applications.userId, userId))
      .orderBy(desc(applications.updatedAt));
  }

  async getApplicationById(id: string): Promise<Application | undefined> {
    const [app] = await db.select().from(applications).where(eq(applications.id, id));
    return app;
  }

  async updateApplicationStatus(id: string, status: string): Promise<void> {
    await db
      .update(applications)
      .set({ status: status as any, updatedAt: new Date() })
      .where(eq(applications.id, id));
  }

  async deleteApplication(id: string): Promise<void> {
    await db.delete(applications).where(eq(applications.id, id));
  }

  // Benchmark operations
  async createBenchmark(benchmark: InsertBenchmark): Promise<Benchmark> {
    const [result] = await db
      .insert(benchmarks)
      .values(benchmark)
      .returning();
    return result;
  }

  async getBenchmarksByApplicationId(applicationId: string): Promise<Benchmark[]> {
    return await db
      .select()
      .from(benchmarks)
      .where(eq(benchmarks.applicationId, applicationId))
      .orderBy(desc(benchmarks.startedAt));
  }

  async getBenchmarksByUserId(userId: string): Promise<Benchmark[]> {
    return await db
      .select()
      .from(benchmarks)
      .where(eq(benchmarks.userId, userId))
      .orderBy(desc(benchmarks.startedAt));
  }

  async updateBenchmark(id: string, data: Partial<Benchmark>): Promise<void> {
    await db
      .update(benchmarks)
      .set(data)
      .where(eq(benchmarks.id, id));
  }

  // Performance metrics operations
  async createPerformanceMetrics(metrics: InsertPerformanceMetrics): Promise<PerformanceMetrics> {
    const [result] = await db
      .insert(performanceMetrics)
      .values(metrics)
      .returning();
    return result;
  }

  async getLatestPerformanceMetrics(applicationId: string): Promise<PerformanceMetrics | undefined> {
    const [result] = await db
      .select()
      .from(performanceMetrics)
      .where(eq(performanceMetrics.applicationId, applicationId))
      .orderBy(desc(performanceMetrics.recordedAt))
      .limit(1);
    return result;
  }

  async getPerformanceMetricsHistory(applicationId: string): Promise<PerformanceMetrics[]> {
    return await db
      .select()
      .from(performanceMetrics)
      .where(eq(performanceMetrics.applicationId, applicationId))
      .orderBy(desc(performanceMetrics.recordedAt));
  }

  // Integration analysis operations
  async upsertIntegrationAnalysis(analysis: InsertIntegrationAnalysis): Promise<IntegrationAnalysis> {
    const [result] = await db
      .insert(integrationAnalysis)
      .values(analysis)
      .onConflictDoUpdate({
        target: integrationAnalysis.applicationId,
        set: {
          ...analysis,
          lastAnalyzedAt: new Date(),
        },
      })
      .returning();
    return result;
  }

  async getIntegrationAnalysis(applicationId: string): Promise<IntegrationAnalysis | undefined> {
    const [result] = await db
      .select()
      .from(integrationAnalysis)
      .where(eq(integrationAnalysis.applicationId, applicationId));
    return result;
  }

  // Reliability metrics operations
  async createReliabilityMetrics(metrics: InsertReliabilityMetrics): Promise<ReliabilityMetrics> {
    const [result] = await db
      .insert(reliabilityMetrics)
      .values(metrics)
      .returning();
    return result;
  }

  async getLatestReliabilityMetrics(applicationId: string): Promise<ReliabilityMetrics | undefined> {
    const [result] = await db
      .select()
      .from(reliabilityMetrics)
      .where(eq(reliabilityMetrics.applicationId, applicationId))
      .orderBy(desc(reliabilityMetrics.recordedAt))
      .limit(1);
    return result;
  }

  async getReliabilityMetricsHistory(applicationId: string): Promise<ReliabilityMetrics[]> {
    return await db
      .select()
      .from(reliabilityMetrics)
      .where(eq(reliabilityMetrics.applicationId, applicationId))
      .orderBy(desc(reliabilityMetrics.recordedAt));
  }

  // Dashboard operations
  async getDashboardStats(userId: string): Promise<{
    totalApplications: number;
    averageResponseTime: number;
    averageUptime: number;
    activeBenchmarks: number;
  }> {
    // Get total applications
    const [appCount] = await db
      .select({ count: sql<number>`count(*)` })
      .from(applications)
      .where(eq(applications.userId, userId));

    // Get average response time from latest performance metrics
    const [avgResponse] = await db
      .select({ avg: sql<number>`avg(${performanceMetrics.responseTime})` })
      .from(performanceMetrics)
      .innerJoin(applications, eq(applications.id, performanceMetrics.applicationId))
      .where(eq(applications.userId, userId));

    // Get average uptime from latest performance metrics
    const [avgUptime] = await db
      .select({ avg: sql<number>`avg(${performanceMetrics.uptime})` })
      .from(performanceMetrics)
      .innerJoin(applications, eq(applications.id, performanceMetrics.applicationId))
      .where(eq(applications.userId, userId));

    // Get active benchmarks count
    const [activeBenchmarks] = await db
      .select({ count: sql<number>`count(*)` })
      .from(benchmarks)
      .where(and(
        eq(benchmarks.userId, userId),
        eq(benchmarks.status, 'running')
      ));

    return {
      totalApplications: appCount.count || 0,
      averageResponseTime: avgResponse.avg || 0,
      averageUptime: avgUptime.avg || 0,
      activeBenchmarks: activeBenchmarks.count || 0,
    };
  }
}

export const storage = new DatabaseStorage();

